import './styles.css';
import { useState } from 'react';

const personalities_initial_state = {
  "jellycat1": 0,
  "jellycat2": 0,
  "jellycat3": 0,
  "jellycat4": 0
};

const gameData = {
  "1": {
    "text": "It's the beginning of the game.",
    "choices": {
      "ok i'll stay here": [2, ["jellycat1, jellycat2"]],
      "no": [2, ["jellycat1, jellycat2"]]
    }
  },
  "2": {
    "text": "We're halfway through the game.",
    "choices": {
      "left": [3, ["jellycat3"]],
      "right": [3, ["jellycat3, jellycat4"]]
    }
  },
  "3": {
    "text": "Game is about to be over!",
    "choices": {
      "left": [0, ["jellycat4"]],
      "right": [0, ["jellycat1, jellycat4"]]
    }
  },
  "0": {}
};

function Game() {
  const [currentState, setCurrentState] = useState(1);
  const [personalities, setPersonalities] = useState(personalities_initial_state);
  const [justStarted, setJustStarted] = useState(true);
  const [storyText, setStoryText] = useState("");
  const [choicesButtons, setChoicesButtons] = useState({});

  function startGame() {
    console.log("a");
    if (justStarted) {
      setJustStarted(false);
    }
    renderState();
  }

  function renderState() {
    setCurrentState(2);
    console.log("b");
    console.log("current state: ", currentState);
    setStoryText(gameData[currentState].text);

    // setChoicesButtons([]);
    const buttonList = [];

    for (const [choice, info] of Object.entries(gameData[currentState].choices)) {
      let nextState = info[0];

      buttonList.push(<div className="choice-button" id="choices" onClick='() => changeState(nextState, info[1])'>{choice}</div>);

      console.log(buttonList);
      // setChoicesButtons(...choicesButtons, button);
      // const button = document.createElement('button');
      // button.textContent = choice;
      // button.className = 'choice-button';
      // let nextState = info[0];
      // button.onclick = () => changeState(nextState, info[1]); //each time you change state you update the personalities dictionary
      // setChoicesButtons(prevState => [...prevState, button])
      // console.log("button", button);
      // console.log(choicesButtons);
    }
  };


  function changeState(newState, selectedPersonalities) {
    // console.log(personalities);
    // console.log("selected personalities %s", selectedPersonalities);
    // // increase personalities by 1
    // const nextPersonalities = {...personalities};
    // for (const key in selectedPersonalities) {
    //   nextPersonalities[key]++;
    // }
    // console.log(selectedPersonalities);
    // console.log("nextpersonalities post set ", nextPersonalities);

    // setPersonalities(nextPersonalities);
    // console.log("personalities post set ", personalities);

    console.log(newState);
    setCurrentState(newState);

    if (currentState === 0) {
      revealMostSelectedVegetable();
    }
    else {
      renderState(currentState);
    }
  }

  function revealMostSelectedVegetable() {
    console.log("calculating fate");
  }

  return (
    <div className="container">
      { justStarted ? (
        <div>
          <div className="title">What Jellycat are you?</div>
          <a href="#" className="start-button" onClick={startGame}>Start</a>
        </div>
      ) : (
        <div>
          <div className="game-container" id="game-container"></div>
          <div className="story-text" id="story-text">{storyText}</div>
          <div className="choices" id="choices" buttonlist={choicesButtons}></div>

          {/* {choicesButtons.map(btn => (
              <div className="choices" id="choices" onClick={btn.onClick}>{btn.choice}</div>
          ))} */}
        </div>
      )}
    </div>
  );
}

export default Game;
