import './styles.css';
import { useState } from 'react';

const gameData = {
  "1": {
    "text": "It's the beginning of the game.",
    "choices": {
      "ok i'll stay here": [1, ["jellycat1", "jellycat2"]],
      "no": [0, ["jellycat1, jellycat2"]]
    }
  }
}

const personalities_obj = {
  "jellycat1": 0,
  "jellycat2": 0
}


function Game() {
  const [currentState, setCurrentState] = useState(1);
  const [personalities, setPersonalities] = useState(personalities_obj);

  function startGame() {
    document.querySelector('.title').style.display = 'none';
    document.querySelector('.start-button').style.display = 'none';
    renderState(1);
  }

  function renderState(state) {
    const storyText = document.getElementById('story-text');
    const choicesContainer = document.getElementById('choices');

    const img = new Image();
    img.src = gameData[currentState].image;

    img.onload = () => {
      storyText.textContent = gameData[currentState].text;
      choicesContainer.innerHTML = '';

      for (const [choice, info] of Object.entries(gameData[currentState].choices)) {
        const button = document.createElement('button');
        button.textContent = choice;
        button.className = 'choice-button';
        let nextState = info[0];
        button.onclick = () => changeState(nextState, info[1]); //each time you change state you update the personalities dictionary
        choicesContainer.appendChild(button);
    }
    };
}


  function changeState(newState, selectedPersonalities) {
    console.log("changing states"); 
    console.log(personalities);
    // increase personalities by 1
    const nextPersonalities = [...personalities]
    selectedPersonalities.forEach(personality => {
      nextPersonalities[personalities]++;
    })
    setPersonalities(nextPersonalities);

    // update current state
    setCurrentState(newState);

    // if current state  === 0 reveal most selected vegetable
    if (currentState === 0) {
      revealMostSelectedVegetable();
    }
    // else render the next state
  }

  function revealMostSelectedVegetable() {
    console.log("calculating fate");
  }

  return (
    <div className="container">
    <div className="title">What Jellycat are you?</div>
        <a href="#" className="start-button" onClick={startGame}>Start</a>
    </div>
  );
}

export default Game;
