import { useState } from 'react';

const gameData = {
  "1": {
    "text": "It's the beginning of the game.",
    "choices": {
      "ok i'll stay here": [2, ["jellycat1", "jellycat2"]],
      "no": [3, ["jellycat1, jellycat2"]]
    }
  }
}

const personalities = {
  "jellycat1": 0,
  "jellycat2": 0
}


function Game() {
  const [currentState, setCurrentState] = useState(1);

  const storyText = document.getElementById('story-text');
  const storyImage = document.getElementById('story-image');
  const choicesContainer = document.getElementById('choices');

  const img = new Image();
  img.src = gameData[currentState].image;

  return (
    <div class ="container">
    <div class="title">What Jellycat are you?</div>
        <a href="#" class="start-button">Start</a>
    </div>
  );
}

export default Game;
